
public class 변수 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Hellow world!");
		
		// 불리언 타입 : 참과 거짓을 표현
		// 데이터 타입 변수명 = 데이터;
		boolean isApple = true; // 1byte
		boolean isAdult = false;
		
		// 수치 -> 정수 타입 : 소숫점이 없는 숫자
		byte b = 17; // 1byte
		short s = 20; // 2byte
		int i = 1089; // 4byte
		long l = 16000; // 8byte
		char c = 65; //글자, 2byte
		
		// 수치 -> 실수 타입 : 소숫점이 있는 숫자
		float f = 3.14f; //f 왜 붙어야? 부동소숫점 // 4byte
		double d = 12.67898; // 8byte
		
	}
}
