
public class 반복문예제3_for문_중간과정 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.print("2단 : ");
		for (int num2 = 1; num2<=9; num2++)
		{
			System.out.print("2*"+ num2 + "="+ (2*num2)+" ");
		}
		System.out.println();
		System.out.print("3단 : ");
		for (int num2 = 1; num2<=9; num2++)
		{
			System.out.print("3*"+ num2 + "="+ (3*num2)+" ");
		}
		System.out.println();
		System.out.print("4단 : ");
		for (int num2 = 1; num2<=9; num2++)
		{
			System.out.print("4*"+ num2 + "="+ (4*num2)+" ");
		}
		// 2단,3단,4단 반복되는 부분.... 
	}

}
